import React, { useState } from 'react';
import './App.css';

function App() {
  const [medicalData, setMedicalData] = useState('');
  const [analysis, setAnalysis] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    setAnalysis('');

    try {
      const response = await fetch('http://localhost:5001/api/analyze', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ medicalData }),
      });

      const data = await response.json();
      
      if (!response.ok) {
        throw new Error(data.error || 'Failed to analyze medical data');
      }

      setAnalysis(data.analysis);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Medical AI Assistant</h1>
        <p>Get AI-powered insights on medical tests and imaging using Gemini</p>
      </header>

      <main className="App-main">
        <form onSubmit={handleSubmit} className="analysis-form">
          <div className="form-group">
            <label htmlFor="medicalData">Enter Medical Data:</label>
            <textarea
              id="medicalData"
              value={medicalData}
              onChange={(e) => setMedicalData(e.target.value)}
              placeholder="Enter MRI results, blood tests, or other medical data..."
              required
            />
          </div>

          <button type="submit" disabled={loading}>
            {loading ? 'Analyzing...' : 'Analyze'}
          </button>
        </form>

        {error && <div className="error-message">{error}</div>}

        {analysis && (
          <div className="analysis-result">
            <h2>Analysis Results</h2>
            <div className="result-content">
              {analysis.split('\n').map((line, index) => (
                <p key={index}>{line}</p>
              ))}
            </div>
          </div>
        )}
      </main>

      <footer className="App-footer">
        <p>Disclaimer: This tool is for medical professionals only. Always verify AI suggestions with clinical judgment.</p>
      </footer>
    </div>
  );
}

export default App;
